mapper.select:
select
  user_id, user_account, user_password, user_name, user_mobile, user_email, user_image_url, user_description, user_password_changed, valid, user_enabled_date, user_disabled_date, user_account_locked, last_login_date, create_time, create_person, modity_time, modify_person, user_sex, user_type, salt, words, user_short_mobile
from imserver.user


        and user_id = #{userId}


        and user_account = #{userAccount}


        and user_password = #{userPassword}


        and user_name = #{userName}


        and user_mobile = #{userMobile}


        and user_email = #{userEmail}


        and user_image_url = #{userImageUrl}


        and user_description = #{userDescription}


        and user_password_changed = #{userPasswordChanged}


        and valid = #{valid}


        and user_enabled_date = #{userEnabledDate}


        and user_disabled_date = #{userDisabledDate}


        and user_account_locked = #{userAccountLocked}


        and last_login_date = #{lastLoginDate}


        and create_time = #{createTime}


        and create_person = #{createPerson}


        and modity_time = #{modityTime}


        and modify_person = #{modifyPerson}


        and user_sex = #{userSex}


        and user_type = #{userType}


        and salt = #{salt}


        and words = #{words}


        and user_short_mobile = #{userShortMobile}

mapper.delete:
delete from imserver.user
where user_id in

    #{item}
